import React, { useState, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddReview = ({ movieId }) => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [rating, setRating] = useState(0);
    const [error, setError] = useState(null);

    // Function to check token presence and validity
    const verifyToken = () => {
        const token = localStorage.getItem('token');
        if (!token) {
            setError('No token found. Please log in.');
            console.log('No token found. Please log in.');
            return null;
        }
        console.log('Token verified:', token);
        return token;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (!username || rating === 0) {
                setError('Please fill out all fields and provide a rating.');
                return;
            }

            const token = verifyToken();
            if (!token) return;

            const reviewData = { username, rating, movieId };
            console.log('Review data:', reviewData);

            // Add new review
            await axios.post(`http://localhost:8081/api/reviews`, reviewData, {
                headers: { Authorization: `Bearer ${token}` },
            });

            navigate(`/movies/${movieId}`);
        } catch (error) {
            console.log('Error adding review:', error);
            if (error.response) {
                setError(error.response.data.message);
            } else if (error.request) {
                setError('No response received from server');
            } else {
                setError('Error adding review: ' + error.message);
            }
        }
    };

    return (
        <div className="add-review-container">
            <h2>Add Review</h2>
            <form onSubmit={handleSubmit}>
                <label>Username:</label>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />

                <label>Rating:</label>
                <div className="rating-stars">
                    {[1, 2, 3, 4, 5].map((star) => (
                        <span
                            key={star}
                            className={`star ${rating >= star ? 'filled' : ''}`}
                            onClick={() => setRating(star)}
                        >
                            â˜…
                        </span>
                    ))}
                </div>

                {error && <div className="error-message">{error}</div>}

                <button type="submit">Add Review</button>
            </form>
        </div>
    );
};

export default AddReview;
