import React, { useState, useCallback, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddMovie = () => {
    const navigate = useNavigate(); // Use for navigation
    const [title, setTitle] = useState('');
    const [director, setDirector] = useState('');
    const [year, setYear] = useState('');
    const [image, setImage] = useState(null);
    const [imageURL, setImageURL] = useState(null);
    const [error, setError] = useState(null);

    // Function to check token presence and validity
    const verifyToken = () => {
        const token = localStorage.getItem('token');
        if (!token) {
            setError('No token found. Please log in.');
            console.log('No token found. Please log in.');
            return null;
        }
        // Add additional token verification logic if needed
        return token;
        console.log('Token verified');
    };

    const uploadImage = useCallback(async () => {
        if (image) {
            const formData = new FormData();
            formData.append('image', image);

            const token = verifyToken();
            if (!token) return;

            try {
                const response = await axios.post('http://localhost:8081/api/upload', formData, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setImageURL(response.data);
            } catch (error) {
                console.log(error);
            }
        }
    }, [image]);

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    useEffect(() => {
        uploadImage();
    }, [uploadImage]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (!title || !director || !year || !imageURL) {
                setError('Please fill out all fields.');
                return;
            }

            const token = verifyToken();
            console.log('Token:', token);
            if (!token) return;

            const movieData = { title, director, year, image: imageURL };

            // Add new movie
            await axios.post('http://localhost:8081/api/movies', movieData, {
                headers: { Authorization: `Bearer ${token}` },
            });

            navigate('/movies');
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message);
            } else if (error.request) {
                setError('No response received from server');
            } else {
                setError('Error adding movie: ' + error.message);
            }
        }
    };

    return (
        <div className="add-movie-container">
            <h2>Add Movie</h2>
            <form onSubmit={handleSubmit}>
                <label>Title:</label>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />

                <label>Director:</label>
                <input
                    type="text"
                    value={director}
                    onChange={(e) => setDirector(e.target.value)}
                    required
                />

                <label>Year:</label>
                <input
                    type="number"
                    value={year}
                    onChange={(e) => setYear(e.target.value)}
                    required
                />

                <label>Image:</label>
                <input
                    type="file"
                    onChange={handleImageChange}
                    accept="image/*"
                    required
                />

                {error && <div className="error-message">{error}</div>}

                <button type="submit">Add Movie</button>
            </form>
        </div>
    );
};

export default AddMovie;
