import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import UserService from '../service/UserService';

function Navbar() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const [profileInfo, setProfileInfo] = useState({});
    const token = localStorage.getItem("token");
    const history = useHistory();

    useEffect(() => {
        const checkAuth = async () => {
            try {
                const authenticated = UserService.isAuthenticated();
                setIsAuthenticated(authenticated);

                if (authenticated) {
                    const admin = UserService.isAdmin();
                    setIsAdmin(admin);

                    const response = await UserService.getYourProfile(token);
                    setProfileInfo(response.ourUsers);

                    // Set timeout for auto-logout after 30 minutes (1800000 milliseconds)
                    const logoutTimeout = setTimeout(logoutUser, 1800000); // 30 minutes
                    return () => clearTimeout(logoutTimeout); // Clear timeout on component unmount
                }
            } catch (error) {
                console.error("Error fetching profile information:", error);
            }
        };

        checkAuth();
    }, [token]);

    const handleLogout = () => {
        const confirmDelete = window.confirm('Are you sure you want to logout this user?');
        if (confirmDelete) {
            logoutUser();
        }
    };

    const logoutUser = () => {
        UserService.logout();
        setIsAuthenticated(false);
        history.push('/'); // Redirect to the homepage after logout
    };

    return (
        <nav>
            <ul>
                {!isAuthenticated && <li><Link to="/">Movies-Reviews-Web</Link></li>}
                {isAuthenticated && <li><Link to="/profile">Profile</Link></li>}
                {isAdmin && <li><Link to="/admin/user-management">User Management</Link></li>}
                {isAuthenticated && <li><Link to="/" onClick={handleLogout}>Logout</Link></li>}
            </ul>
        </nav>
    );
}

export default Navbar;
