import React, { useState } from 'react';
import axios from 'axios';

const AddMovie = () => {
    const [title, setTitle] = useState('');
    const [director, setDirector] = useState('');
    const [year, setYear] = useState('');
    const [image, setImage] = useState(null); // State to hold the selected image file
    const [error, setError] = useState(null);

    const handleImageChange = (e) => {
        setImage(e.target.files[0]); // Update the image state with the selected file
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Check if all required fields are filled
            if (!title || !director || !year || !image) {
                setError('Please fill out all fields.');
                return;
            }

            // Retrieve token from localStorage
            const token = localStorage.getItem('token');

            // Create FormData object to send the file and other data
            const formData = new FormData();
            formData.append('title', title);
            formData.append('director', director);
            formData.append('year', year);
            formData.append('image', image); // Append selected image file to FormData

            // Send POST request to add movie
            const response = await axios.post(
                'http://localhost:8081/public/api/movies',
                formData,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                     
                    },
                }
            );

            console.log('Movie added successfully:', response.data);
            // Optionally: Redirect or show a success message
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message); // Display server error message
            } else if (error.request) {
                setError('No response received from server');
            } else {
                setError('Error adding movie: ' + error.message);
            }
            console.error('Error adding movie:', error);
        }
    };

    return (
        <div className="add-movie-container">
            <h2>Add Movie</h2>
            <form onSubmit={handleSubmit}>
                <label>Title:</label>
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />

                <label>Director:</label>
                <input type="text" value={director} onChange={(e) => setDirector(e.target.value)} required />

                <label>Year:</label>
                <input type="number" value={year} onChange={(e) => setYear(e.target.value)} required />

                <label>Image:</label>
                <input type="file" onChange={handleImageChange} accept="image/*" required />
                
                {error && <div className="error-message">{error}</div>}

                <button type="submit">Add Movie</button>
            </form>
        </div>
    );
};

export default AddMovie;
