// components/MovieManagementPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function MovieManagementPage() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    // Fetch movies data when the component mounts
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const token = localStorage.getItem('token'); // Retrieve the token from localStorage
      const response = await axios.get('http://your-api-url/movies', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setMovies(response.data.ourMoviesList); // Assuming the list of movies is under the key 'ourMoviesList'
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
  };

  const deleteMovie = async (movieId) => {
    try {
      // Prompt for confirmation before deleting the movie
      const confirmDelete = window.confirm('Are you sure you want to delete this movie?');

      const token = localStorage.getItem('token'); // Retrieve the token from localStorage
      if (confirmDelete) {
        await axios.delete(`http://your-api-url/movies/${movieId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        // After deleting the movie, fetch the updated list of movies
        fetchMovies();
      }
    } catch (error) {
      console.error('Error deleting movie:', error);
    }
  };

  return (
    <div className="movie-management-container">
      <h2>Movies Management Page</h2>
      <button className='reg-button'><Link to="/add-movie">Add Movie</Link></button>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Director</th>
            <th>Release Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {movies.map(movie => (
            <tr key={movie.id}>
              <td>{movie.id}</td>
              <td>{movie.title}</td>
              <td>{movie.director}</td>
              <td>{movie.releaseDate}</td>
              <td>
                <button className='delete-button' onClick={() => deleteMovie(movie.id)}>Delete</button>
                <button><Link to={`/update-movie/${movie.id}`}>
                  Update
                </Link>
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default MovieManagementPage;
