import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewAllReviews = () => {
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true); // Add loading state
    const token = localStorage.getItem("token");
    const { movieId } = useParams();
    console.log(movieId, 'search');
    const [error, setError] = useState(null);


    useEffect(() => {
        fetchMovieDataById(movieId);
    }, [movieId]);
    console.log('movieId:', movieId);
    const fetchMovieDataById = async (movieId) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('No token found. Please log in.');
                return;
            }

            const response = await axios.get(`http://localhost:8081/api/movie/${movieId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            const { title, director, year } = response.data;
            setMovie({ title, director, year }); // Set movie details to state

        } catch (error) {
            console.error('Error fetching movie data:', error);
            setError('Error fetching movie data');
        }
    };

    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const response = await axios.get('http://localhost:8081/api/reviews', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setReviews(response.data);
                setLoading(false); // Set loading to false after data is fetched
            } catch (error) {
                console.error('Error fetching reviews:', error);
                setLoading(false); // Set loading to false on error
            }
        };

        if (token) {
            fetchReviews();
        }
    }, [token]);

    return (
        <div className="view-all-reviews">
            <header className="App-header">
                <h1>Movie Review App</h1>
            </header>
            <main>
                <div className="reviews">
                    <h2>Movie Reviews</h2>
                    {loading ? (
                        <p>Loading reviews...</p> // Display loading message
                    ) : reviews.length > 0 ? (
                        <table>
                            <thead>
                                <tr>
                                    <th>Username</th>
                                    <th>Content</th>
                                    <th>Rating</th>
                                    <th>Reviewed On</th>
                                    <th>Movie ID</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reviews.map(review => (
                                    <tr key={review.id}>
                                        <td>{review.username}</td>
                                        <td>{review.content}</td>
                                        <td>{review.rating}</td>
                                        <td>{new Date(review.createdAt).toLocaleDateString()}</td>
                                        <td>{review.movie.id}</td> {/* Displaying movieId */}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>No reviews available.</p>
                    )}
                </div>
            </main>
        </div>
    );
};

export default ViewAllReviews;
