import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';


const AddMovie = () => {
    const { id } = useParams(); // get the id from the URL
    const [title, setTitle] = useState('');
    const [director, setDirector] = useState('');
    const [year, setYear] = useState('');
    const [image, setImage] = useState(null);
    const [imageURL, setImageURL] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (id) {
            // Fetch movie details if editing an existing movie
            axios.get(`http://localhost:8081/api/movies/${id}`)
                .then(response => {
                    const { title, director, year, image } = response.data;
                    setTitle(title);
                    setDirector(director);
                    setYear(year);
                    setImageURL(image);
                })
                .catch(error => console.log(error));
        }
    }, [id]);

    const uploadImage = useCallback(async () => {
        if (image) {
            const formData = new FormData();
            formData.append('image', image);

            try {
                const response = await axios.post('http://localhost:8081/api/upload', formData);
                setImageURL(response.data);
            } catch (error) {
                console.log(error);
            }
        }
    }, [image]);

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    useEffect(() => {
        uploadImage();
    }, [uploadImage]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (!title || !director || !year || !imageURL) {
                setError('Please fill out all fields.');
                return;
            }

            const token = localStorage.getItem('token');
            const movieData = {
                title,
                director,
                year,
                image: imageURL,
            };

            if (id) {
                // Update movie
                await axios.put(`http://localhost:8081/api/movies/${id}`, movieData, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
            } else {
                // Add new movie
                await axios.post('http://localhost:8081/api/movies', movieData, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
            }

            window.location.href = '/movies';
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message);
            } else if (error.request) {
                setError('No response received from server');
            } else {
                setError('Error adding movie: ' + error.message);
            }
        }
    };

    return (
        <div className="add-movie-container">
            <h2>{id ? 'Edit Movie' : 'Add Movie'}</h2>
            <form onSubmit={handleSubmit}>
                <label>Title:</label>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />

                <label>Director:</label>
                <input
                    type="text"
                    value={director}
                    onChange={(e) => setDirector(e.target.value)}
                    required
                />

                <label>Year:</label>
                <input
                    type="number"
                    value={year}
                    onChange={(e) => setYear(e.target.value)}
                    required
                />

                <label>Image:</label>
                <input
                    type="file"
                    onChange={handleImageChange}
                    accept="image/*"
                    required={!id}
                />

                {error && <div className="error-message">{error}</div>}

                <button type="submit">{id ? 'Update Movie' : 'Add Movie'}</button>
            </form>
        </div>
    );
};

export default AddMovie;
