import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewMovieByID = ({ movieId }) => {
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true); // Add loading state
    const token = localStorage.getItem("token");

    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchReviews = async () => {
            setLoading(true); // Set loading to true before fetching data
            try {
                const response = await axios.get(`http://localhost:8081/api/reviews/movie/${movieId}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setReviews(response.data);
                setLoading(false); // Set loading to false after data is fetched
            } catch (error) {
                console.error('Error fetching reviews:', error);
                setLoading(false); // Set loading to false on error
            }
        };

        if (token) {
            fetchReviews();
        }
    }, [token, movieId]);

    return (
        <div className="view-movie-by-id">
            <header className="App-header">
                <h1>Movie Review App</h1>
            </header>
            <main>
                <div className="reviews">
                    <h2>Reviews for Movie ID: {movieId}</h2>
                    {loading ? (
                        <p>Loading reviews...</p> // Display loading message
                    ) : reviews.length > 0 ? (
                        <table>
                            <thead>
                                <tr>
                                    <th>Username</th>
                                    <th>Content</th>
                                    <th>Rating</th>
                                    <th>Reviewed On</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reviews.map(review => (
                                    <tr key={review.id}>
                                        <td>{review.username}</td>
                                        <td>{review.content}</td>
                                        <td>{review.rating}</td>
                                        <td>{new Date(review.createdAt).toLocaleDateString()}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>No reviews available for this movie.</p>
                    )}
                </div>
            </main>
        </div>
    );
};

export default ViewMovieByID;
